SELECT
  appointment_id, patient_id, provider_id, `date`, appoint_time, status, create_time, update_time
FROM
  Appointment
WHERE
  patient_id = #{patientId}
  AND start_time >= #{start}
  AND start_time <= #{end}

SELECT
  appointment_id, patient_id, provider_id, `date`, appoint_time, status, create_time, update_time
FROM
  Appointment
WHERE
  provider_id = #{providerId}
  AND start_time >= #{start}
